        margin: 1em auto;
        max-width: 800px;
        width: 95%;
        font: 18px/1.5 sans-serif;
    }

    .example-wrapper code {
        background: #F5F5F5;
        padding: 2px 6px;
    }
</style>

<div class=\"container\">

    <table class=\"table\">
        <tr>
            <th>Name</th>
            <th>Description</th>
            <th>Category</th>
            <th>Edit</th>
            <th>Delete</th>
        </tr>
        {% for category in categoryList %}
        <tr>
            <td><a href=\"#\">{{category.name}}</a></td>
            <td>{{category.description}}</td>
            <td>{{category.slug}}</td>
            <td><a href=\"{{path('app_category_edit',{'id':category.id})}}\">
                    Edit category</a>
            </td>
            <td>
                <a href=\"{{path('app_category_delete',{'id':category.id})}}\">
                    Delete category</a>
            </td>
        </tr>

        <br>

        {% endfor %}
    </table>


</div>
{% endblock %}", "category/index.html.twig", "C:\\Users\\croger\\Documents personnel\\Symfony\\Symfony_ROGERC\\Evaluation_ROGERC\\templates\\category\\index.html.twig");
